pragma solidity ^0.8.0;

import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Burnable.sol";

import "./Operator.sol";

/*
__________                             .___   ___________.__
\______   \_____     ______  ____    __| _/   \_   _____/|__|  ____  _____     ____    ____   ____
 |    |  _/\__  \   /  ___/_/ __ \  / __ |     |    __)  |  | /    \ \__  \   /    \ _/ ___\_/ __ \
 |    |   \ / __ \_ \___ \ \  ___/ / /_/ |     |     \   |  ||   |  \ / __ \_|   |  \\  \___\  ___/
 |______  /(____  //____  > \___  >\____ |     \___  /   |__||___|  /(____  /|___|  / \___  >\___  >
        \/      \/      \/      \/      \/         \/             \/      \/      \/      \/     \/
*/
contract GODtoken is ERC20Burnable, Operator {
    using SafeMath for uint256;

    // TOTAL MAX SUPPLY = 50,000 bSHAREs
    uint256 public constant FARMING_POOL_REWARD_ALLOCATION = 1000 ether;


    bool public rewardPoolDistributed = false;

    constructor(

    ) ERC20("GODS", "GODs") {
        _mint(msg.sender, 10 ether); // mint 10 GodTOkens for initial pools deployment
    }
    /**
     * @notice distribute to reward pool (only once)
     */
    function distributeReward(address _farmingIncentiveFund) external onlyOperator {
        require(!rewardPoolDistributed, "only can distribute once");
        require(_farmingIncentiveFund != address(0), "!_farmingIncentiveFund");
        rewardPoolDistributed = true;
        _mint(_farmingIncentiveFund, FARMING_POOL_REWARD_ALLOCATION);
    }

    function burn(uint256 amount) public override {
        super.burn(amount);
    }

    function governanceRecoverUnsupported(
        IERC20 _token,
        uint256 _amount,
        address _to
    ) external onlyOperator {
        _token.transfer(_to, _amount);
    }
}","keccak256":"0xdccf909f58908810b520144cdf9d596bba9e22131cccbc2e0a6707ce7f0d9b91","license":"MIT"},"contracts/GodNFT.sol":{"content":"// SPDX-License-Identifier: BUSL-1.1

pragma solidity ^0.8.4;

import "./token/onft/ONFT.sol";
import "./interfaces/IGodNFT.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

contract GodNFT is ONFT, IGodNFT {
    uint public nextMintId;
    uint public maxSupply;
    address public managerAddress;

    modifier onlyManager() {
        require(msg.sender == managerAddress, "Only manager can call this function.");
        _;
    }

    /// @param _layerZeroEndpoint handles message transmission across chains
    constructor(address _layerZeroEndpoint, uint _maxSupply) ONFT("GodNFT", "BNFT", _layerZeroEndpoint) {
        nextMintId = 0;
        maxSupply = _maxSupply;
    }

    /// @notice Mint your ONFT
    function mintFor(address minter) external override onlyManager {
        require(nextMintId <= maxSupply, "ONFT: Max Mint limit reached");

        uint newId = nextMintId;
        nextMintId++;

        _safeMint(minter, newId);
    }

    function setManagerAddress(address _managerAddress) public onlyOwner {
        managerAddress = _managerAddress;
    }
    function getTokenList(address account) external view returns (uint256[] memory) {
        require(msg.sender != address(0));
        require(account != address(0));

        address selectedAccount = msg.sender;
        if (owner() == msg.sender)
            selectedAccount = account;

        uint256 count = balanceOf(selectedAccount);
        uint256[] memory tokenIdList = new uint256[](count);

        if (count == 0)
            return tokenIdList;

        uint256 cnt = 0;
        for (uint256 i = 0; i < nextMintId ; i++) {

            if (_exists(i) && (ownerOf(i) == selectedAccount)) {
                tokenIdList[cnt++] = i;
            }

            if (cnt == count)
                break;
        }

        return tokenIdList;
    }
}","keccak256":"0x91511217be6f8a8da5a832102064942824319eec70e4912bc10259513d3bb80c","license":"BUSL-1.1"},"contracts/NFTStaker.sol":{"content":"// SPDX-License-Identifier: MIT LICENSE

pragma solidity ^0.8.4;

import "./GODtoken.sol";
import "./GodNFT.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

contract NFTStaker is Ownable, IERC721Receiver {
using SafeERC20 for IERC20;

  uint256 public totalStaked;
  IERC20 token;
  IERC721 nft;

 // struct to store a stake's token, owner, and earning values
  struct Staker {
    uint256 timestamp;
    address owner;
  }

  mapping(uint256 => Staker) public stakers; 
  // Enumeration
  mapping(address => mapping(uint256 => uint256)) public ownedStakes; // (address, index) => tokenid
  mapping(uint256 => uint256) public ownedStakesIndex; // tokenId => index in its owner's stake list
  mapping (address => uint256) public ownedStakesBalance;

   constructor(address _nft, address _token) { 
    nft = IERC721(_nft);
    token = IERC20(_token);
  }

   receive() external payable {}

  function stake(uint256[] calldata tokenIds) external {
    uint256 tokenId;
    address _owner = msg.sender;
    totalStaked += tokenIds.length;
    for (uint i = 0; i < tokenIds.length; i++) {
      tokenId = tokenIds[i];
      require(nft.ownerOf(tokenId) == msg.sender, "not your token");
      require(stakers[tokenId].timestamp == 0, "already staked");

      stakers[tokenId] = Staker({
        owner: _owner,
        timestamp: uint256(block.timestamp)
      });
      
      uint256 length = ownedStakesBalance[_owner];
      ownedStakes[_owner][length] = tokenId;
      ownedStakesIndex[tokenId] = length;
      ownedStakesBalance[_owner]++;
      
      nft.transferFrom(msg.sender, address(this), tokenId);
      token.safeTransfer(msg.sender, 1);
    }
  }

  function unstake(address _staker, uint256[] calldata tokenIds) external {
    uint256 tokenId;
    address _owner = msg.sender;
    totalStaked -= tokenIds.length;
      require(token.balanceOf(msg.sender) >= tokenIds.length, "not enough GODS tokens");
    for (uint i = 0; i < tokenIds.length; i++) {
      tokenId = tokenIds[i];
      Staker memory staked = stakers[tokenId];
      require(staked.owner == msg.sender, "not an owner");
    
      delete stakers[tokenId];

      uint256 lastTokenIndex = ownedStakesBalance[_owner] - 1;
      uint256 tokenIndex = ownedStakesIndex[tokenId];

      if (tokenIndex != lastTokenIndex) {
          uint256 lastTokenId = ownedStakes[_owner][lastTokenIndex];
          ownedStakes[_owner][tokenIndex] = lastTokenId;
          ownedStakesIndex[lastTokenId] = tokenIndex;
      }

      delete ownedStakesIndex[tokenId];
      delete ownedStakes[_owner][lastTokenIndex];

      ownedStakesBalance[msg.sender]--;
      nft.transferFrom(address(this), _staker, tokenId);
      token.safeTransferFrom(msg.sender, address(this), 1);
    }
  }

  // should never be used inside of transaction because of gas fee
  function batchedStakesOfOwner(
      address _owner,
      uint256 _offset,
      uint256 _maxSize
  ) public view returns (uint256[] memory) {
      if (_offset >= ownedStakesBalance[_owner]) {
          return new uint256[](0);
      }

      uint256 outputSize = _maxSize;
      if (_offset + _maxSize >= ownedStakesBalance[_owner]) {
          outputSize = ownedStakesBalance[_owner] - _offset;
      }
      uint256[] memory outputs = new uint256[](outputSize);

      for (uint256 i = 0; i < outputSize; i++) {
          uint256 tokenId = ownedStakes[_owner][_offset + i];
          outputs[i] = tokenId;
      }
      return outputs;
  }

  function onERC721Received(
        address,
        address from,
        uint256,
        bytes calldata
    ) external pure override returns (bytes4) {
      require(from == address(0x0), "Cannot send nfts to Pit");
      return IERC721Receiver.onERC721Received.selector;
    }
  
}","keccak256":"0x3c3fed71d72b1830647b9abed95d089960f592cbe22905adbb93a4fd0033a131","license":"MIT LICENSE"},"contracts/Operator.sol":{"content":"// SPDX-License-Identifier: MIT

pragma solidity ^0.8.0;

import "@openzeppelin/contracts/utils/Context.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

contract Operator is Context, Ownable {
    address private _operator;

    event OperatorTransferred(address indexed previousOperator, address indexed newOperator);

    constructor() {
        _operator = _msgSender();
        emit OperatorTransferred(address(0), _operator);
    }

    function operator() public view returns (address) {
        return _operator;
    }

    modifier onlyOperator() {
        require(_operator == msg.sender, "operator: caller is not the operator");
        _;
    }

    function isOperator() public view returns (bool) {
        return _msgSender() == _operator;
    }

    function transferOperator(address newOperator_) public onlyOwner {
        _transferOperator(newOperator_);
    }

    function _transferOperator(address newOperator_) internal {
        require(newOperator_ != address(0), "operator: zero address given for new operator");
        emit OperatorTransferred(address(0), newOperator_);
        _operator = newOperator_;
    }

    function _renounceOperator() public onlyOwner {
        emit OperatorTransferred(_operator, address(0));
        _operator = address(0);
    }
}","keccak256":"0xbbb478d094d0250aae444aac3879ac4a883457800e5b8da0ccd2b2a3d03539bb","license":"MIT"},"contracts/interfaces/IGodNFT.sol":{"content":"// SPDX-License-Identifier: MIT
